import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import Editor from "@monaco-editor/react";

function Test({    name, name2, handleSaveFile2 }) {
  const [overlap, SetOverlap] = useState(null);
  const location = useLocation();
  let [file,setFileContent] = useState(null);
  let [file2, setFile2Content] = useState(null);
    h
  useEffect(() => {
    const { from } = location.state;
    console.log(from);
    const val = { 'data': from };
    console.log(val);

    if (!from) {
      return;
    }

    fetch("http://localhost:3500/file", {
      method: "POST",
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(val),
    })
      .then(res => {
        if (!res.ok) {
          throw res.statusText;
        }
        return res.json();
      })
      .then(data => {
        setFileContent(data['data']['0']);
        console.log(file);
        name(data['data']['name1']);
        name2(data['data']['name2']);
        setFile2Content(data['data']['1']);

        if (data['data']['overlap'] !== undefined) {
          SetOverlap(data["data"]["overlap"]);
        }
      })
      .catch(err => {
        console.error(err);
      });
  }, [location.state, name, name2]);

  return (
    <div id='editor'>
      <h1>{overlap}</h1>
      <div className='App2'>
        <div>
          <Editor
            height="60vh"
            width="50vw"
            language="yaml"
            theme="vs-dark"
            value={file || 'Initial content for Editor 1'} 
          />
          <button onClick={handleSaveFile}>Save 1</button>
        </div>
        <div>
          <Editor
            id="editor2"
            height="60vh"
            width="50vw"
            language="yaml"
            theme="vs-dark"
            value={file2 || 'Initial content for Editor 2'} 
          />
          <button onClick={handleSaveFile2}>Save 2</button>
        </div>
      </div>
    </div>
  );
}

export default Test;
