import './App.css';
import { BsFillCloudUploadFill, BsCheckLg } from "react-icons/bs";
import {  AiFillFileText  } from "react-icons/ai";
import { useState } from "react";
import {Watch} from "react-loader-spinner";
import ReactDOM from 'react-dom/client';
import ItemList from './Componnent/List_item';
import {BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';

function App() {

  return (
    <Router>
    <div className="App">
    <div className='wrapper'>
      <h1>Uploading Files</h1>
      <div onClick={()=>{
          const fileInput = document.querySelector("input");
          fileInput.click();
      }} className='wrapper2'>
          <BsFillCloudUploadFill style={{
            fontSize:"50px",
            color:"black",
            }}></BsFillCloudUploadFill>

          <input hidden={true} onChange={(e)=> {setFile(e.target.files[0])
          document.querySelector("section").style.display = "block";
          if(e.target.files[0].name !== undefined)
            document.querySelector(".name").innerText= e.target.files[0].name;  


          } } type="file"/>
      </div>
      <section hidden={true} >
      <div id='span-list'>
      <li className='rows' >
          <AiFillFileText style={{
            fontSize:"30px",
          }}></AiFillFileText>
          <div className='content'>
            <div className='detail'  style={{overflow_y:"scroll"}}>
              <span className='name'>fikjdejefhjlfcjlchejklle_01</span>
            </div>
          </div>
          <div  hidden={true} id="check">
          <BsCheckLg style={{
            fontSize:"30px",
            color:"green",
          }}></BsCheckLg>
          </div>

        </li>

      </div>

      </section>
      <div style={{
        display:'flex',
        alignContent:'center',
        justifyContent:'center',
      }}>
      <Watch
                height="80"
                width="80"
                radius="48"
                ariaLabel="watch-loading"
                wrapperStyle={{}}
                wrapperClassName=""
                color='black'
                visible={!loading}
              />
      </div>
      <div style={{
        display:'flex',
        alignContent:'center',
        justifyContent:'center',
      }}> <button className="upload" onClick={ handleUpload }>Upload</button></div>

   <div style={{
        display:'flex',
        alignContent:'center',
        justifyContent:'center',
      }}>
      <Routes>
        <Route path="/rules" element= {<ItemList/>}>
        </Route>
      </Routes>
      <button className="upload" onClick={()=>navigate("/rules")}>redirect</button></div>

     <div style={{
          display:'flex',
          alignContent:'center',
          justifyContent:'center',
        }}>


   { msg && <span style={{
    marginTop:"15px",
    color:'black',
   }}>{msg}</span> }
   </div>


    </div>

    </div>
    </Router>
  );
}

export default App;
