import React, { useState, useEffect,useRef } from 'react';
import { useLocation } from 'react-router-dom';
import Editor from "@monaco-editor/react";

function Test() {
  const [overlap, SetOverlap] = useState(null);
  const location = useLocation();
  let [file,setFileContent] = useState(null);
  let [file2, setFile2Content] = useState(null);
  let [name, setName] = useState(null)
  let [name2, setName2] = useState(null)
  const monacoEditorRef = useRef(null);
  const monacoEditor2Ref = useRef(null);

  useEffect(() => {
    const { from } = location.state;

    const val = { 'data': from };


    if (!from) {
      return;
    }

    fetch("http://localhost:3500/file", {
      method: "POST",
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(val),
    })
      .then(res => {
        if (!res.ok) {
          throw res.statusText;
        }
        return res.json();
      })
      .then(data => {
        setFileContent(data['data']['0']);

        setName(data['data']['name1']);
        setName2(data['data']['name2']);
        setFile2Content(data['data']['1']);


        if (data['data']['overlap'] !== undefined) {
          SetOverlap(data["data"]["overlap"]);
        // SetOverlap('patterns')
        }
      })
      .catch(err => {
        console.error(err);
      });
  }, [location.state]);

  const handleSaveFile = () => {
    // Create a Blob with the content of the first Ace Editor (file)
    const blob = new Blob([file], { type: 'text/plain' });

    // Create a URL for the Blob
    const url = URL.createObjectURL(blob);

    // Create an anchor element for downloading
    const a = document.createElement('a');
    a.href = url;
    a.download = name + '.yaml';
    a.click();

    // Clean up the URL object
    URL.revokeObjectURL(url);
  };
  const handleSaveFile2 = () => {
    // Create a Blob with the content of the first Ace Editor (file)
    const blob = new Blob([file2], { type: 'text/plain' });

    // Create a URL for the Blob
    const url = URL.createObjectURL(blob);

    // Create an anchor element for downloading
    const a = document.createElement('a');
    a.href = url;
    a.download = name2 + '.yaml';
    a.click();

    // Clean up the URL object
    URL.revokeObjectURL(url);
  };

  useEffect(() => {
    if (overlap && monacoEditorRef.current) {
      const editor = monacoEditorRef.current;
      const model = editor.getModel();

      if (model) {
        const content = model.getValue();
        const regex = new RegExp(overlap, 'g');
        let match;
        const decorations = [];
        const decorationOptions = {
          isWholeLine: false,
          className: 'highlighted-text',
        };

        while ((match = regex.exec(content)) !== null) {
          decorations.push({
            range: new Range(
              model.getPositionAt(match.index),
              model.getPositionAt(match.index + overlap.length)
            ),
            options: decorationOptions,
          });
        }

        editor.deltaDecorations([], decorations);
        editor.layout();
      }
    }
  }, [overlap]);

  const handleEditorChange = (value, event) => {
    setFileContent(value);
  };

  const handleEditor2Change = (value, event) => {
    setFile2Content(value);
  };


  return (
    <div id='editor'>
      <h1>{overlap}</h1>
      <div className='App2'>
        <div>
          <Editor
            height="60vh"
            width="50vw"
            language="yaml"
            theme="vs-dark"
            value={file} 
            onChange={handleEditorChange}
            onMount={editor => {
                // Store the Monaco Editor instance in the ref
                monacoEditorRef.current = editor;
            }}
          />
          <button onClick={handleSaveFile}>Save 1</button>
        </div>
        <div>
          <Editor
            id="editor2"
            height="60vh"
            width="50vw"
            language="yaml"
            theme="vs-dark"
            value={file2} 
            onChange={handleEditor2Change}
            onMount={editor => {
                // Store the Monaco Editor instance in the ref
                monacoEditor2Ref.current = editor;
            }}
          />
          <button onClick={handleSaveFile2}>Save 2</button>
        </div>
      </div>
    </div>
  );
}

export default Test;
