import React, { useState, useEffect,useRef } from 'react';
import { useLocation } from 'react-router-dom';
import Editor from "@monaco-editor/react";
import CodeMirror from "@uiw/react-codemirror";

function Test() {
  const [overlap, SetOverlap] = useState(null);
  const location = useLocation();
  let [file,setFileContent] = useState(null);
  let [file2, setFile2Content] = useState(null);
  let [name, setName] = useState(null)
  let [name2, setName2] = useState(null)
  let monacoEditorRef = useRef(null);
  let monacoEditor2Ref = useRef(null);
  let decorationsRef = useRef([]);

  useEffect(() => {
    const { from } = location.state;

    const val = { 'data': from };


    if (!from) {
      return;
    }

    fetch("http://localhost:3500/file", {
      method: "POST",
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(val),
    })
      .then(res => {
        if (!res.ok) {
          throw res.statusText;
        }
        return res.json();
      })
      .then(data => {
        setFileContent(data['data']['0']);
        console.log(file)

        setName(data['data']['name1']);
        setName2(data['data']['name2']);
        setFile2Content(data['data']['1']);


        if (data['data']['overlap'] !== undefined && !data['data']['overlap'].includes("Connection")) {
          SetOverlap(data["data"]["overlap"]);
        // SetOverlap('xxpatterns')
        }
      })
      .catch(err => {
        console.error(err);
      });
  }, [location.state]);

  const handleSaveFile = () => {
    // Create a Blob with the content of the first Ace Editor (file)
    const blob = new Blob([file], { type: 'text/plain' });

    // Create a URL for the Blob
    const url = URL.createObjectURL(blob);

    // Create an anchor element for downloading
    const a = document.createElement('a');
    a.href = url;
    a.download = name + '.yaml';
    a.click();

    // Clean up the URL object
    URL.revokeObjectURL(url);
  };
  const handleSaveFile2 = () => {
    // Create a Blob with the content of the first Ace Editor (file)
    const blob = new Blob([file2], { type: 'text/plain' });

    // Create a URL for the Blob
    const url = URL.createObjectURL(blob);

    // Create an anchor element for downloading
    const a = document.createElement('a');
    a.href = url;
    a.download = name2 + '.yaml';
    a.click();

    // Clean up the URL object
    URL.revokeObjectURL(url);
  };
  function escapeRegExp(string) {
    // Escape special characters except for "$" and "n"
    return string.replace(/[.*+?^${}()|[\]\\]/g, '\\$&').replace(/\$/g, '$').replace(/n/g, 'n');
  }


  function findSub(string1, string2) {
    // Then, you can proceed with your existing logic to find other substrings
    let list = [];
    let tmp = "";
    let unescape = "";

    for (let i = 0; i < string1.length; i++) {
      tmp += escapeRegExp(string1[i]);
      unescape += string1[i];

      if (!string2.includes(unescape)) {
        tmp = tmp.slice(0, -1);
        unescape = unescape.slice(0, -1);

        if (unescape.length > 1 && !["OR", "AND", "NOT"].includes(unescape)) {
          list.push(unescape);
        }

        tmp = escapeRegExp(string1[i]);
        unescape = string1[i];
      }
    }

    if (unescape.length > 1 && !["OR", "AND", "NOT"].includes(unescape)) {
      list.push(unescape);
    }

    console.log(list);
    return list;
  }

  function highlight(){

    if (overlap && monacoEditorRef.current) {

        const editor = monacoEditorRef.current;
        console.log(decorationsRef)
        editor.deltaDecorations(decorationsRef.current, []);
        const model = editor.getModel();
        if (model) {
            let matchList = findSub(overlap,model.getValue())

            for(let i in matchList){
                const matches = model.findMatches(matchList[i]);
                const decorationOptions = {
                  isWholeLine: false,
                  className: 'highlighted-text',
                };
                const decorations = matches.map(match => ({
                  range: match.range,
                  options: decorationOptions,
                }));
                decorationsRef.current =  editor.deltaDecorations([], decorations);
                // Refresh the editor layout
                editor.layout();
              }
            }
    }

  }
  useEffect(() => {
    if (overlap && monacoEditorRef.current) {
      const editor = monacoEditorRef.current;
      const model = editor.getModel();

      if (model) {
        let matchList = findSub(overlap,model.getValue())

        for(let i in matchList){
            const matches = model.findMatches(matchList[i]);
            const decorationOptions = {
              isWholeLine: false,
              className: 'highlighted-text',
            };
            const decorations = matches.map(match => ({
              range: match.range,
              options: decorationOptions,
            }));

            decorationsRef.current =decorations
            editor.deltaDecorations([], decorations);
            // Refresh the editor layout
            editor.layout();
          }
        }

    }
    if (overlap && monacoEditor2Ref.current) {
      const editor2 = monacoEditor2Ref.current;
      const model2 = editor2.getModel();

      if (model2) {
        let matchList = findSub(overlap,model2.getValue())

        for(let i in matchList){
            const matches2 = model2.findMatches(matchList[i]);

        const decorationOptions = {
          isWholeLine: false,
          className: 'highlighted-text',
        };
        const decorations2 = matches2.map(match => ({
          range: match.range,
          options: decorationOptions,
        }));

        editor2.deltaDecorations([], decorations2);

        // Refresh the editor layout
        editor2.layout();
      }
    }
    }
  }, [overlap]);

  const handleEditorChange = (value, event) => {
    setFileContent(value);
    highlight();
  };

  const handleEditor2Change = (value, event) => {
    setFile2Content(value);
    // updateHighlight();
  };


  return (
    <div id='editor'>
      <h1>{overlap}</h1>
      <div className='App2'>
        <div>
          <CodeMirror
            height="60vh"
            width="50vw"
            language="yaml"
            theme="vs-dark"
            value={file} 
            onChange={handleEditorChange}
            onMount={editor => {
                // Store the Monaco Editor instance in the ref
                monacoEditorRef.current = editor;
            }}
          />
          <button onClick={handleSaveFile}>Save 1</button>
        </div>
        <div>
          <Editor
            id="editor2"
            height="60vh"
            width="50vw"
            language="yaml"
            theme="vs-dark"
            value={file2} 
            onChange={handleEditor2Change}
            onMount={editor => {
                // Store the Monaco Editor instance in the ref
                monacoEditor2Ref.current = editor;
            }}
          />
          <button onClick={handleSaveFile2}>Save 2</button>
        </div>
      </div>
    </div>
  );
}

export default Test;
