import { React, useState } from 'react';
import { useLocation } from 'react-router-dom';
import AceEditor from "react-ace";
import "ace-builds/src-noconflict/mode-java";
import "ace-builds/src-noconflict/theme-github";
import "ace-builds/src-noconflict/ext-language_tools";

function Test() {
  var [fileContent, setFileContent] = useState(null);
  var [newfile,setNew ] = useState(null);
  var [newfile2,setNew2] = useState(null);
  var [file2Content, setFile2Content] = useState(null);
  const [name,setName] = useState(null);
  const [name2,setName2] = useState(null);
  const [overlap, SetOverlap] = useState(null);
  const location = useLocation();


  const handleSaveFile = () => {
    // Create a Blob with the content of the first Ace Editor (file)
    const blob = new Blob([newfile], { type: 'text/plain' });

    // Create a URL for the Blob
    const url = URL.createObjectURL(blob);

    // Create an anchor element for downloading
    const a = document.createElement('a');
    a.href = url;
    a.download = name + '.yaml';
    a.click();

    // Clean up the URL object
    URL.revokeObjectURL(url);
  };

  const handleSaveFile2 = () => {
    // Create a Blob with the content of the second Ace Editor (file2)
    const blob = new Blob([newfile2], { type: 'text/plain' });

    // Create a URL for the Blob
    const url = URL.createObjectURL(blob);

    // Create an anchor element for downloading
    const a = document.createElement('a');
    a.href = url;
    a.download = name2+".yaml"
    a.click();

    // Clean up the URL object
    URL.revokeObjectURL(url);
  };

  try {
    const { from } = location.state;
    console.log(from);
    const val = { 'data': from };
    console.log(val);

    if (!from) {
      return (
        <div className='App'>
          <p>No file Selected</p>
        </div>
      );
    }

    fetch("http://localhost:3500/file", {
      method: "POST",
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(val),
    })
      .then(res => {
        if (!res.ok) {
          throw res.statusText;
        }
        return res.json();
      })
      .then(data => {
        setFileContent(data['data']['0']);
        setName(data['data']['name1'])
        setName2(data['data']['name2'])
        setFile2Content(data['data']['1']);

        if (data['data']['overlap'] !== undefined) {
          SetOverlap(data["data"]["overlap"]);
        }
      })
      .catch(err => {
        console.error(err);
      });

    return (
      <div id='editor'>
        <h1>{overlap}</h1>
        <div className='App2'>import CodeMirror from "@uiw/react-codemirror";
          <div>
            <AceEditor

              mode="java"
              theme="github"
              name="editor"
              width='50vw'
              height='60vh'
              fontSize='16pt'

              value={fileContent}
              editorProps={{ $blockScrolling: true }}
            />
            <button onClick={handleSaveFile}>Save 1</button>
          </div>
          <div>
            <AceEditor
              mode="java"
              theme="github"
              name="editor"
              width='50vw'
              height='60vh'
              fontSize='16pt'
              value={file2Content}

              editorProps={{ $blockScrolling: true }}
            />
            <button onClick={handleSaveFile2}>Save 2</button>
          </div>
        </div>
      </div>
    );
  } catch {
    return (
      <div className='App'>
        <p>No file Selected</p>
      </div>
    );
  }
}

export default Test;
